package com.eoe.commenttogelter.ui;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ScrollView;import com.eoe.commenttogelter.R;import com.eoe.commenttogelter.ui.custom.PullToRefreshView;import com.eoe.commenttogelter.ui.custom.PullToRefreshView.PullToRefreshListener;public class MainFragment extends Fragment {	PullToRefreshView pullToRefreshView;	ScrollView scroll;		@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,			Bundle savedInstanceState) {		View view = LayoutInflater.from(getActivity()).inflate(R.layout.frame_main, null);				scroll = (ScrollView) view.findViewById(R.id.scroll);		pullToRefreshView = (PullToRefreshView) view.findViewById(R.id.pull);		pullToRefreshView.setOnRefreshListener(new PullToRefreshListener() {			@Override			public void onRefresh() {				try {					Thread.sleep(3000);				} catch (InterruptedException e) {					e.printStackTrace();				}				pullToRefreshView.finishRefreshing();			}		}, 0);		return view;	}}